import fetch from 'isomorphic-fetch'

class Index extends React.Component {
    static async getInitialProps() {

        let stories;

        try {
            const response = await fetch("https://node-hnapi.herokuapp.com/news?page=1/n");
            stories = await response.json();

        } catch (err) {
            console.log(err)
            stories = [];
        }
        
        return { stories }
    }
    render() {
        return (
            <div>Hacker Next</div>
        )
    }
}

export default Index;
